
public class Max3Nos {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
      int i =7,j=5,k=4;
      if (i > j)
      {
    	 if ( i > k )
    		System.out.println("I is the highest");
    	 else 
    	 { 
    		 System.out.println("K is the highest");
    	 }
    	 
      }
    else 
    	 {
    		 if ( j > k )
    	    		System.out.println("J is the highest");
    	    	 else { 
    	    		 System.out.println("K is the highest");
    	 }
      }
      
	}

}
